var videoElm=document.getElementById("VideoPlayer"),videoDescr=document.getElementById("video-description"),playElm=document.getElementById("play");$(playElm).on("click",function(){1==videoElm.paused?videoElm.play():videoElm.pause()}),$(videoElm).on("click",function(){1==videoElm.paused?videoElm.play():videoElm.pause()}),videoElm.onplay=function(){videoDescr.style.display="none"},videoElm.onpause=function(){videoDescr.style.display="block"};